import { createFeatureSelector, createSelector } from '@ngrx/store';
import { selectAll } from '../reducers/user.reducer';
import { vaccineCardsFeatureKey } from '../reducers/vaccine-card.reducer';
export const selectVaccineCardState = createFeatureSelector(vaccineCardsFeatureKey);
export const selectAllVaccineCards = createSelector(selectVaccineCardState, selectAll);
export const selectVaccineCardByPetId = id => createSelector(selectVaccineCardState, state => state.entities[id]);
