import { createFeatureSelector, createSelector } from '@ngrx/store';
import { ordersFeatureKey, selectAll } from '../reducers/order.reducer';
export const selectOrdersState = createFeatureSelector(ordersFeatureKey);
export const selectAllOrders = createSelector(selectOrdersState, selectAll);
export const selectOrderById = id => createSelector(selectAllOrders, allOrders => {
  if (allOrders) {
    return allOrders.find(p => p.id == id);
  }

  return null;
});
